import React, { useState, useEffect } from 'react';
import { useBookValidation } from './Validator';
import ErrorAlert from './common/ErrorAlert';

const AddBookModal = ({ book = null, isEdit = false, onSave, onClose }) => {
  const initialFormState = {
    title: '',
    author: '',
    ISBN: '',
    publishedDate: '',
    genre: '',
    copiesAvailable: 1
  };

  const [formData, setFormData] = useState(initialFormState);
  const [errors, setErrors] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  
  // Get validation utilities from the context
  const { validate, normalizeDate } = useBookValidation();

  // If editing, populate form with book data
  useEffect(() => {
    if (book) {
      setFormData({
        title: book.title || '',
        author: book.author || '',
        ISBN: book.ISBN || '',
        publishedDate: normalizeDate(book.publishedDate) || '',
        genre: book.genre || '',
        copiesAvailable: book.copiesAvailable || 1
      });
    }
  }, [book, normalizeDate]);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type } = e.target;
    
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'number' ? parseInt(value, 10) || 0 : value
    }));
    
    if (submitted) {
      // Re-validate on change after first submission
      validateForm();
    }
  };

  // Validate the form
  const validateForm = () => {
    const result = validate(formData);
    setErrors(result.isValid ? null : result.errorElements);
    return result.isValid;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    
    if (validateForm()) {
      onSave(formData);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-md max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold text-gray-800">
              {isEdit ? 'Edit Book' : 'Add New Book'}
            </h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
          
          {errors && <ErrorAlert messages={errors} />}
          
          <form onSubmit={handleSubmit}>
            <div className="space-y-4">
              <div>
                <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                  Title *
                </label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  placeholder="Book title"
                />
              </div>
              
              <div>
                <label htmlFor="author" className="block text-sm font-medium text-gray-700">
                  Author *
                </label>
                <input
                  type="text"
                  id="author"
                  name="author"
                  value={formData.author}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  placeholder="Author name"
                />
              </div>
              
              <div>
                <label htmlFor="ISBN" className="block text-sm font-medium text-gray-700">
                  ISBN *
                </label>
                <input
                  type="text"
                  id="ISBN"
                  name="ISBN"
                  value={formData.ISBN}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                  placeholder="10 or 13 digit ISBN"
                />
                <p className="mt-1 text-xs text-gray-500">
                  Must be a valid 10 or 13 digit ISBN
                </p>
              </div>
              
              <div>
                <label htmlFor="publishedDate" className="block text-sm font-medium text-gray-700">
                  Published Date *
                </label>
                <input
                  type="date"
                  id="publishedDate"
                  name="publishedDate"
                  value={formData.publishedDate}
                  onChange={handleChange}
                  max={new Date().toISOString().split('T')[0]}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                />
              </div>
              
              <div>
                <label htmlFor="genre" className="block text-sm font-medium text-gray-700">
                  Genre
                </label>
                <select
                  id="genre"
                  name="genre"
                  value={formData.genre}
                  onChange={handleChange}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                >
                  <option value="">Select genre</option>
                  <option value="Fiction">Fiction</option>
                  <option value="Non-fiction">Non-fiction</option>
                  <option value="Academic">Academic</option>
                  <option value="Children">Children's Books</option>
                  <option value="Reference">Reference</option>
                  <option value="Self-help">Self-help</option>
                </select>
              </div>
              
              <div>
                <label htmlFor="copiesAvailable" className="block text-sm font-medium text-gray-700">
                  Copies Available
                </label>
                <input
                  type="number"
                  id="copiesAvailable"
                  name="copiesAvailable"
                  value={formData.copiesAvailable}
                  onChange={handleChange}
                  min="0"
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                />
                {formData.genre === "Academic" && (
                  <p className="mt-1 text-xs text-red-500">
                    Academic books require at least 5 copies
                  </p>
                )}
              </div>
            </div>
            
            <div className="mt-6 flex justify-end space-x-3">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md text-sm font-medium"
              >
                {isEdit ? 'Save Changes' : 'Add Book'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddBookModal;
