import React, { useState } from 'react';
import BookCard from './BookCard';
import Spinner from './common/Spinner';
import ErrorAlert from './common/ErrorAlert';
import AddBookModal from './AddBookModal';

const BookList = ({ 
  books = [], 
  isLoading = false, 
  error = null, 
  onAddBook,
  onEditBook,
  onDeleteBook 
}) => {
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);

  // Handle edit book
  const handleEditBook = (book) => {
    setSelectedBook(book);
    setShowAddModal(true);
  };

  // Handle modal close
  const handleCloseModal = () => {
    setShowAddModal(false);
    setSelectedBook(null);
  };

  // Handle saving a book (new or edited)
  const handleSaveBook = (bookData) => {
    if (selectedBook) {
      onEditBook({ ...bookData, id: selectedBook.id });
    } else {
      onAddBook(bookData);
    }
    handleCloseModal();
  };

  if (isLoading) {
    return (
      <div className="py-10">
        <Spinner size="lg" />
      </div>
    );
  }

  if (error) {
    return <ErrorAlert messages={error} />;
  }

  return (
    <div className="container mx-auto py-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">Book Collection</h2>
        <button
          onClick={() => setShowAddModal(true)}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors duration-200"
        >
          Add New Book
        </button>
      </div>

      {books.length === 0 ? (
        <div className="bg-gray-50 rounded-lg p-8 text-center">
          <p className="text-gray-500">No books found. Add your first book to get started.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {books.map((book) => (
            <BookCard
              key={book.id}
              book={book}
              onEdit={handleEditBook}
              onDelete={onDeleteBook}
            />
          ))}
        </div>
      )}

      {showAddModal && (
        <AddBookModal
          book={selectedBook}
          isEdit={!!selectedBook}
          onSave={handleSaveBook}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default BookList;
