import React, { useState } from 'react';
import Spinner from './common/Spinner';
import ErrorAlert from './common/ErrorAlert';

// BorrowReturnButton component that handles borrowing/returning a book
const BorrowReturnButton = ({ bookId, isBorrowed, onBorrow, onReturn, disabled }) => {
  const [loading, setLoading] = useState(false);
  
  const handleAction = async () => {
    setLoading(true);
    try {
      if (isBorrowed) {
        await onReturn(bookId);
      } else {
        await onBorrow(bookId);
      }
    } catch (error) {
      console.error('Action failed:', error);
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <button
      onClick={handleAction}
      disabled={disabled || loading}
      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
        isBorrowed
          ? 'bg-amber-100 text-amber-800 hover:bg-amber-200'
          : 'bg-green-100 text-green-800 hover:bg-green-200'
      } ${disabled || loading ? 'opacity-50 cursor-not-allowed' : ''}`}
    >
      {loading ? (
        <span className="flex items-center">
          <Spinner size="sm" />
          <span className="ml-2">Processing...</span>
        </span>
      ) : (
        isBorrowed ? 'Return Book' : 'Borrow Book'
      )}
    </button>
  );
};

// Main UserManagement component
const UserManagement = ({ 
  books = [],
  userBooks = [],
  isLoading = false,
  error = null,
  onBorrowBook,
  onReturnBook
}) => {
  // For this demo, we'll just show a list of books that can be borrowed/returned
  // In a real application, this would likely be a more complex component with user authentication
  
  // Helper function to check if a book is borrowed by current user
  const isBookBorrowed = (bookId) => {
    return userBooks.some(book => book.id === bookId);
  };
  
  // Handle book borrowing
  const handleBorrow = async (bookId) => {
    try {
      await onBorrowBook(bookId);
      return true;
    } catch (error) {
      console.error('Failed to borrow book:', error);
      return false;
    }
  };
  
  // Handle book return
  const handleReturn = async (bookId) => {
    try {
      await onReturnBook(bookId);
      return true;
    } catch (error) {
      console.error('Failed to return book:', error);
      return false;
    }
  };
  
  if (isLoading) {
    return (
      <div className="py-6">
        <Spinner size="md" />
      </div>
    );
  }
  
  if (error) {
    return <ErrorAlert messages={error} />;
  }
  
  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="px-6 py-4 border-b border-gray-200">
        <h3 className="text-lg font-medium text-gray-900">Your Book Management</h3>
        <p className="mt-1 text-sm text-gray-500">
          Borrow books from our collection or return your current loans.
        </p>
      </div>
      
      <div className="px-6 py-4">
        <h4 className="text-md font-medium text-gray-700 mb-3">Available Books</h4>
        
        {books.length === 0 ? (
          <p className="text-sm text-gray-500">No books available for borrowing.</p>
        ) : (
          <div className="divide-y divide-gray-200">
            {books.map(book => (
              <div key={book.id} className="py-4 flex justify-between items-center">
                <div>
                  <h5 className="text-sm font-medium text-gray-900">{book.title}</h5>
                  <p className="text-sm text-gray-500">by {book.author}</p>
                  <p className="text-xs text-gray-400 mt-1">
                    {book.copiesAvailable} {book.copiesAvailable === 1 ? 'copy' : 'copies'} available
                  </p>
                </div>
                <BorrowReturnButton
                  bookId={book.id}
                  isBorrowed={isBookBorrowed(book.id)}
                  onBorrow={handleBorrow}
                  onReturn={handleReturn}
                  disabled={book.copiesAvailable === 0 && !isBookBorrowed(book.id)}
                />
              </div>
            ))}
          </div>
        )}
      </div>
      
      <div className="px-6 py-4 bg-gray-50">
        <h4 className="text-md font-medium text-gray-700 mb-3">Your Borrowed Books</h4>
        
        {userBooks.length === 0 ? (
          <p className="text-sm text-gray-500">You haven't borrowed any books yet.</p>
        ) : (
          <div className="space-y-4">
            {userBooks.map(book => (
              <div key={book.id} className="bg-white p-4 rounded border border-gray-200">
                <div className="flex justify-between items-start">
                  <div>
                    <h5 className="text-sm font-medium text-gray-900">{book.title}</h5>
                    <p className="text-sm text-gray-500">by {book.author}</p>
                    <p className="text-xs text-gray-400 mt-1">
                      Borrowed on {new Date(book.borrowedDate).toLocaleDateString()}
                    </p>
                  </div>
                  <BorrowReturnButton
                    bookId={book.id}
                    isBorrowed={true}
                    onBorrow={handleBorrow}
                    onReturn={handleReturn}
                  />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default UserManagement;
export { BorrowReturnButton };
