import React from 'react';

const BookCard = ({ book, onEdit, onDelete }) => {
  const {
    title,
    author,
    ISBN,
    publishedDate,
    genre,
    copiesAvailable
  } = book;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
      <div className="p-5">
        <div className="flex justify-between items-start">
          <h3 className="text-xl font-semibold text-gray-800">{title}</h3>
          <span className="px-2 py-1 text-xs rounded-full bg-blue-100 text-blue-800">
            {genre || 'Uncategorized'}
          </span>
        </div>
        
        <p className="text-gray-600 mt-2">by {author}</p>
        
        <div className="mt-4 text-sm text-gray-500">
          <p>ISBN: {ISBN}</p>
          <p>Published: {new Date(publishedDate).toLocaleDateString()}</p>
          <p className={`font-semibold ${copiesAvailable > 0 ? 'text-green-600' : 'text-red-600'}`}>
            {copiesAvailable > 0 ? `${copiesAvailable} copies available` : 'Out of stock'}
          </p>
        </div>
        
        <div className="mt-5 flex space-x-2">
          <button
            onClick={() => onEdit(book)}
            className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-800 text-sm rounded-md transition-colors duration-200"
          >
            Edit
          </button>
          <button
            onClick={() => onDelete(book.id)}
            className="px-3 py-1 bg-red-100 hover:bg-red-200 text-red-800 text-sm rounded-md transition-colors duration-200"
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default BookCard;
